import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState('Chargement...');

  useEffect(() => {
    setMessage('React fonctionne ! Ajoute tes composants maintenant.');
  }, []);

  return (
    <div className="app">
      <div className="container">
        <header className="app-header">
          <h1>üìù Ma Todo List</h1>
          <p>Test de fonctionnement</p>
        </header>
        
        <div style={{
          background: 'white', 
          padding: '30px', 
          borderRadius: '15px',
          marginTop: '20px',
          boxShadow: '0 5px 15px rgba(0,0,0,0.1)'
        }}>
          <h3>Status : {message}</h3>
          <button 
            onClick={() => alert('React fonctionne !')}
            style={{
              background: 'linear-gradient(135deg, #667eea, #764ba2)',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              cursor: 'pointer',
              marginTop: '10px'
            }}
          >
            Test React
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
